import axios from 'axios';
import { connect } from "react-redux";

export class Blog extends Component {
    constructor(){
        super();
        this.state = {
            blog: [],
            editMode: false 
        }
    }

    componentDidMount(){
        axios.get(`/api/blog/${this.props.match.params.blog_id}`).then(response => {
            this.setState({
                blog : response.data
            })
        }).catch(err => console.log(err))
    }
  render() {
      let {blog} = this.state;
