const INITIAL_STATE = {
    user: '',
    admin: false
}

const LOGIN_USER = 'LOGIN_USER';
const UPDATE_ADMIN = 'UPDATE_ADMIN';

export default function reducer( state = INITIAL_STATE,  action ){
    switch (action.type){
        case LOGIN_USER:

            return Object.assign({}, state,{user: action.payload, admin: action.payload});
        default: 
            return state;
    }
}

export function userFn(username){

    return {
        type: LOGIN_USER,
        payload: username, admin
    };
};

