import React, { Component } from "react";
import axios from "axios";
import { connect } from "react-redux";

export class Blog extends Component {
  constructor() {
    super();
    this.state = {
      blog: [],
      editMode: false
    };
  }

  componentDidMount() {
    axios
      .get(`/api/blog/${this.props.match.params.blog_id}`)
      .then(response => {
        this.setState({
          blog: response.data
        });
      })
      .catch(err => console.log(err));
  }
  render() {
    let { blog } = this.state;
    console.log(blog);
    let mappedBlog = blog.map(blog => {
      return (
        <div className="singleBlog" key={blog.blog_id} style={{display:'flex', flexDirect:'column'}}>
          <h1>{blog.blog_title}</h1>
          <p>{blog.blog_body}</p>
          {this.props.userObj.admin === true ? (
            <button
              style={{ float: "right", background: "white" }}
              onClick={() => {
                this.setState({
                  editMode: this.state.editMode == false ? true : false
                });
              }}
            >
              Edit Post
            </button>
          ) : null}
        </div>
      );
    });
    console.log(this.state.editMode);
    return <div>{mappedBlog}</div>;
  }
}

const mapStateToProps = state => {
  return {
    userObj: state
  };
};

export default connect(mapStateToProps)(Blog);
