import React, { Component } from "react";
import "./home.css";
import axios from "axios";

export class Home extends Component {
  constructor() {
    super();
    this.state = {
      user: null,
      message: null
    };
  }

  getMessage = error =>
  error.response
    ? error.response.data
      ? error.response.data.message
      : JSON.stringify(error.response.data, null, 2)
    : error.message;

  login = () => {
    console.log('hit')
    this.setState({ message: null });
    const username = this.refs.username.value;
    const password = this.refs.password.value;
    axios
      .post("/login", {
        username,
        password
      })
      .then(response => {
        this.setState({ user: response.data });
      })
      .catch(error => {
        this.setState({
          message: "Something went wrong: " + this.getMessage(error)
        });
      });
  };

  render() {
    return (
      <div className="home">
        <form onSubmit={e => e.preventDefault()}>
          <h1>Username: </h1>
          <input type="text" ref="username" />
          <h1>Password: </h1>
          <input type="password" ref="password" />
          <input type="submit" value="Log In" onClick={this.login} />
        </form>
        <img
          src="https://marketplace.canva.com/MADGyYVoY2c/4/screen/canva-aerial-photography-of-cityscape-MADGyYVoY2c.jpg"
          alt="placeholder"
        />
      </div>
    );
  }
}

export default Home;
